// HasDownloadJobs applies the HasEdge predicate on the "download_jobs" edge.
func HasDownloadJobs() predicate.DownloadClient {
	return predicate.DownloadClient(func(s *sql.Selector) {
		step := sqlgraph.NewStep(
			sqlgraph.From(Table, FieldID),
			sqlgraph.Edge(sqlgraph.O2M, false, DownloadJobsTable, DownloadJobsColumn),
		)
		sqlgraph.HasNeighbors(s, step)
	})
}

// HasDownloadJobsWith applies the HasEdge predicate on the "download_jobs" edge with a given conditions (other predicates).
func HasDownloadJobsWith(preds ...predicate.DownloadJob) predicate.DownloadClient {
	return predicate.DownloadClient(func(s *sql.Selector) {
		step := newDownloadJobsStep()
		sqlgraph.HasNeighborsWith(s, step, func(s *sql.Selector) {
			for _, p := range preds {
				p(s)
			}
		})
	})
}

// And groups predicates with the AND operator between them.
func And(predicates ...predicate.DownloadClient) predicate.DownloadClient {
	return predicate.DownloadClient(sql.AndPredicates(predicates...))
}

// Or groups predicates with the OR operator between them.
func Or(predicates ...predicate.DownloadClient) predicate.DownloadClient {
	return predicate.DownloadClient(sql.OrPredicates(predicates...))
}

// Not applies the not operator on the given predicate.
func Not(p predicate.DownloadClient) predicate.DownloadClient {
	return predicate.DownloadClient(sql.NotPredicates(p))
}
